css:
/*defaults*/

img,video {
	max-width: 100%;
}

video, audio { 
	margin: 0 auto; 
	display: block; 
	text-align: center;
	font-family: sans-serif;
	font-weight: bold;
	color: #bcbcbc;
}

audio img {
	height: 1em; 
}

p { 
	text-indent: 0;
	margin-bottom: .5em;
	margin-top: .5em;
	line-height: 1.2em; 
	}

hr {
	margin:1.5em; }		

/*styles dropdown*/

p.normal {
	text-indent: 0;
	margin-bottom: .5em;
	margin-top: .5em;
	line-height: 1.2em; 
	}

p.indent {
	text-indent: 7%;
	margin-bottom: 0;
	margin-top: 0; 
	line-height: 1.2em; 
	}

h1 { 
	margin: 0; 
	font-size: 3em;
	line-height: 1.2em; 
	}

h2 { 
	margin: 0;
	font-size: 2em; 
	line-height: 1.2em; 
	}

h3 { 
	margin: 0;
	font-size: 1.5em; 
	line-height: 1.2em; 
	}		

p.italic {
	margin: 1em 7%;
	font-style: italic; 
	line-height: 1.2em; 
	}

p.shaded {
	margin: 1em 7%;
	padding: .75em;
	background-color: #ececec;
	border-radius: 2px; 
	line-height: 1.2em; 
	}	

p.outlined {
	margin: 1em 7%;
	padding: .75em;
	border-width: 4px;
	border: 1px dotted #000;
	line-height: 1.2em; 
	}	

p.narrowtopmargin {
	margin-top: 2em;
	}

p.mediumtopmargin {
	margin-top: 4em;
	}

p.widetopmargin {
	margin-top: 8em;
	}

p.mediumlinespace {
	line-height: 1.6em;
	}

p.widelinespace {
	line-height: 2em;
	}

p.smallimage {
	width: 40%;
	margin-left: 30%;
	margin-right: 30%;
	font-size: 80%;
	}

p.smallimage img {
	width: 100%;
	}

p.mediumimage {
	width: 60%;
	margin-left: 20%;
	margin-right: 20%;
	font-size: 80%;
	}

p.mediumimage img {
	width: 100%;
	}	

p.largeimage {
	width: 80%;
	margin-left: 10%;
	margin-right: 10%;
	font-size: 80%; 
	}	

p.largeimage img {
	width: 100%;
	}	

p.fullimage {
	width: 100%;
	font-size: 80%; 
	}	

p.fullimage img {
	width: 100%;
	}	

.monospace{
	font-family: monospace;
}
.sans-serif{
	font-family: sans-serif;
}
.strike {
	text-decoration: line-through;
	}

.underline {
	text-decoration: underline;
	}

.smallcaps { 
	font-size: 80%; 
	text-transform: uppercase; 
	}	

.super {
	vertical-align: super;
	font-size: 70%;
	line-height: 0;
	}	

.sub {
	vertical-align: sub;
	font-size: 70%;
	line-height: 0;
	}	

/* table of contents */

nav {
	text-align: center;
	margin-top: 4em;
	}	

nav ol {
	list-style-type: none;
	padding: 0;
	}

nav h1 {
	font-size: 2em; }

nav li {
	line-height: 2em;
	margin:0;
	list-style-type: none;
 }
